       // Initialise the LocationManager object
       locationManager = (LocationManager) getSystemService(Context.LOCATION_SERVICE);

       // Register three callbacks to listen for the best location, location updates, and time updates
       locationManager.requestLocationUpdates(LocationManager.GPS_PROVIDER, 0, 0, locationProviderListener);
       locationManager.requestLocationUpdates(LocationManager.GNSS_PROVIDER, 0, 0, locationProviderListener);
